  const srcFilter = this.get('inputs')[currentFilter.name];

  const newInput = _.assign(_.cloneDeep(srcFilter), currentFilter);

  this.set({
    'showFilterForm': false,
    'currentFilter': {},
  });

  this.push('activeInputs', newInput);

  filterAndRender(this.get('activeInputs'));
